settings.custom_liquid:
{%- liquid
  assign threshold = 99900
  assign cart_total = cart.total_price | default: 0
-%}

<div
  id="delivery-note-wrapper"
  class="delivery-note{% if cart_total < threshold %} hide{% endif %}"
  style="margin: 1.5rem 0; padding: 0 2.5rem;"
>
  <label for="delivery-note" class="form__label">
    Optional delivery note <span class="caption-large">(max 120 characters)</span>
  </label>

  <textarea
    id="delivery-note"
    name="attributes[Delivery Note]"
    rows="4"
    maxlength="120"
    placeholder="e.g. Please deliver after 6 PM or leave at gate"
  >{{ cart.attributes['Delivery Note'] | escape }}</textarea>

  <p class="caption character-counter text-body" style="text-align: right; margin-top: 0.5rem;">
    <span id="char-count">
      {{ cart.attributes['Delivery Note'] | size | default: 0 }}
    </span>/120
  </p>
</div>

<style>
  .delivery-note.hide { display: none; }
  .delivery-note textarea {
    width: 100%;
    padding: 6px;
  }
  .delivery-note textarea:focus {
    outline: none;
    box-shadow: none;
  }
</style>

{% render 'delivery-snippet-js' %}
